const payload = msg.payload.map(({relay_position, state}) => {
    return {
        relay_position,
        state: state ? 1 : 0
    }
});

return [payload];

const { error } = msg;
let { message } = error;

try {
    message = JSON.parse(message);
} catch(err) {}

return {
    ...msg,
    payload: {
        error: {
            ...error,
            message
        }
    }
};

const {
    payload,
    relay_position
} = msg;

const {
    state,
    actor,
    ...rest
} = payload;

if ([state, relay_position].includes(undefined) || Object.keys(rest).length > 0) {
    throw `Invalid payload`
}

let state_gpio = {...global.get('state_gpio') || {}};

let item_state = state_gpio[relay_position] || {};
item_state.state = state;
item_state.actors ||= [];

if(actor) {
    const item_actor = item_state.actors.find(({actor: a}) => a === actor);
    if (state && !item_actor) {
        item_state.actors.push({
            actor,
            state
        })
    } else if(!state && item_actor) {
        item_state.actors = item_state.actors.filter(({actor: a}) => a !== actor);
    }
}

state_gpio[relay_position] = item_state;

global.set('state_gpio', state_gpio);

return msg;

const {
    payload
} = msg;

if(!Array.isArray(payload)) {
    throw { type: 'invalid_payload' }
};

const relays_state = {
    ...global.get('relays_state') || {}
};

payload.forEach(item => {
    const {
        relay_position,
        state,
        actor,
        mode,
        ...rest
    } = item;

    const item_state = relays_state[relay_position] || {};

    if (
        [state, relay_position].includes(undefined)
        || Object.keys(rest).length > 0
        || (mode && !['subscribe', 'unsubscribe'].includes(mode))
        || (!!mode !== !!actor)
    ) {
        throw { type: 'invalid_payload' }
    };

    if (mode === 'subscribe' && actor && item_state.actors) {
        const conflicting_actors = item_state.actors.filter(({ state: s }) => s !== state);
        if (conflicting_actors.length > 0) {
            throw {
                type: 'conflicting_actors_found',
                conflicting_actors
            }
        }
    };
})

return msg;

const {
    payload
} = msg;

let relays_state = {...global.get('relays_state') || {}};

payload.forEach(item => {
    const {
        relay_position,
        state,
        actor,
        mode
    } = item;

    let item_state = relays_state[relay_position] || {};
    item_state.state = state;
    item_state.actors ||= [];

    if(actor) {
        if(mode === 'subscribe') {
            const item_actor = item_state.actors.find(({actor: a}) => a === actor);
            if (!item_actor) {
                item_state.actors.push({
                    actor,
                    state
                })
            }
        } else if(mode === 'unsubscribe') {
            item_state.actors = item_state.actors.filter(({actor: a}) => a !== actor);
        }
    }

    relays_state[relay_position] = item_state;
})

global.set('relays_state', relays_state);

return msg;

const root_scripts_path = env.get('PEKAWAY_SCRIPTS_PATH');
const filename = `liontronBmsInfo.py`;
const { device_id } = msg.payload;

if(!root_scripts_path) {
    throw `$PEKAWAY_SCRIPTS_PATH is not set`
} else if(!device_id) {
    throw `\`device_id\` is required`
} else {
    return {
        payload: `${root_scripts_path}/${filename} -d ${device_id}`
    }
};

const {
    payload
} = msg;

if([2,3].includes(payload.mode || !global.get('gps_info'))) {
    global.set('gps_info', msg.payload);
};

const gps = global.get('gps_info');
const modes = {
    0: 'unknown',
    1: 'no_fix',
    2: '2d',
    3: '3d'
};

if(!gps) {
    throw `GPS info not available`
};

return {
    ...msg,
    payload: {
        mode: modes[gps.mode],
        time: gps.time,
        longitude: gps.lon,
        latitude: gps.lat,
        altitude_hae: gps.altHAE,
        altitude_msl: gps.altMSL,
        speed: gps.speed,
        direction: gps.track,
        climb: gps.climb,
        estimated_errors: {
            longitude: gps.epx,
            latitude: gps.epy,
            altitude: gps.epv,
            speed: gps.eps,
            direction: gps.epd,
            climb: gps.epc
        }
    }
};

const root_scripts_path = env.get('PEKAWAY_SCRIPTS_PATH');
const filename = `ds18b20.py`;

if(!root_scripts_path) {
    throw `$PEKAWAY_SCRIPTS_PATH is not set`
} else {
    return {
        ...msg,
        payload: `${root_scripts_path}/${filename}`
    }
};

const {
    payload
} = msg;

let parsedPayload = {};

payload
    .split("\n")
    .filter(s => !!s)
    .forEach(string => {
        const [
            index,
            temperature,
            unit
        ] = string.trim().split(/[\s,\t,\n]+/);

        parsedPayload[parseInt(index)] = {
            temperature: parseFloat(temperature),
            unit
        }
    });

return {
    ...msg,
    payload: parsedPayload
};

const {
    payload,
    relay_position
} = msg;

const { state } = payload;

return {
    payload: [
        {
            relay_position,
            state: state === 0 ? 1 : 0,
            // actor: 'test',
            // mode: 'subscribe'
        },
        {
            relay_position: relay_position+1,
            state: state === 0 ? 1 : 0,
            // actor: 'test',
            // mode: 'subscribe'
        }
    ]
}
