const relays_state = {
    ...global.get('relays_state') || {}
};

payload.forEach(item => {
    const {
        relay_type,
        relay_position,
        state,
        actor,
        mode,
        vendor_id,
        mqtt_topic,
        ...rest
    } = item;

    const relay_type_state = relays_state[relay_type] || {};
    const item_state = relay_type_state[relay_position] || {};

    if (
        [relay_position, relay_type].includes(undefined)
        || !['wifi_relay', 'relay'].includes(relay_type)
        || Object.keys(rest).length > 0
        || (
            relay_type === 'wifi_relay' 
            && [vendor_id, mqtt_topic].includes(undefined)
        )
        || (
            relay_type === 'relay'
            && (!!vendor_id || !!mqtt_topic)
        )
        || !['subscribe', 'unsubscribe'].includes(mode)
        || !actor
        || (state !== undefined && mode === 'unsubscribe')
        || (state === undefined && mode === 'subscribe')
    ) {
        throw { type: 'invalid_payload' }
    };

    if (
        mode === 'subscribe'
        && state !== item_state.state
        && (item_state.actors || []).length > 0
    ) {
        throw {
            type: 'conflicting_actors_found',
            relay_type,
            relay_position,
            actors: item_state.actors
        }
    };
})

return msg;

const {
