} = msg;

let relays_state = {...global.get('relays_state') || {}};

const newPayload = payload.map(item => {
    const {
        relay_type,
        relay_position,
        vendor_id,
        mqtt_topic,
        state,
        actor,
        mode
    } = item;

    let relay_type_state = relays_state[relay_type] || {};
    let mqtt_topic_state = relay_type_state[mqtt_topic] || {};

    let item_state;
    if(relay_type === 'relay') {
        item_state = relay_type_state[relay_position] || {};
    } else if(relay_type === 'wifi_relay') {
        item_state = mqtt_topic_state[relay_position] || {};
    }

    item_state.actors ||= [];

    if(mode === 'subscribe') {
        item_state.state = state;
        if (!item_state.actors.find(({actor: a}) => a === actor)) {
            item_state.actors.push({ actor })
        }
    } else if(mode === 'unsubscribe') {
        item_state.actors = item_state.actors.filter(({actor: a}) => a !== actor);
        if(item_state.actors.length === 0) {
            item_state.state = false;
        }
    }

    if(relay_type === 'relay') {
        relay_type_state[relay_position] = item_state;
    } else if(relay_type === 'wifi_relay') {
        mqtt_topic_state[relay_position] = item_state;
        relay_type_state[mqtt_topic] = mqtt_topic_state;
    }

    relays_state[relay_type] = relay_type_state;

    return {
        relay_type,
        relay_position,
        vendor_id,
        mqtt_topic,
        state: item_state.state
    }
});

global.set('relays_state', relays_state);

return {
