const {
    payload
} = msg;

if(!Array.isArray(payload)) {
    throw { type: 'invalid_payload' }
};

const relays_state = {
    ...global.get('relays_state') || {}
};

payload.forEach(item => {
    const {
        relay_type,
        relay_position,
        state,
        actor,
        mode,
        vendor_id,
        mqtt_topic,
        ...rest
    } = item;

    const relay_type_state = relays_state[relay_type] || {};
    const item_state = relay_type_state[relay_position] || {};

    if (
        [relay_position, relay_type].includes(undefined)
        || !['wifi_relay', 'relay'].includes(relay_type)
        || Object.keys(rest).length > 0
        || (
            relay_type === 'wifi_relay' 
            && [vendor_id, mqtt_topic].includes(undefined)
        )
        || (
            relay_type === 'relay'
            && (!!vendor_id || !!mqtt_topic)
        )
        || !['subscribe', 'unsubscribe'].includes(mode)
        || !actor
        || (state !== undefined && mode === 'unsubscribe')
        || (state === undefined && mode === 'subscribe')
    ) {
        throw { type: 'invalid_payload' }
    };

    if (
        mode === 'subscribe'
        && state !== item_state.state
        && (item_state.actors || []).length > 0
    ) {
        throw {
            type: 'conflicting_actors_found',
            relay_type,
            relay_position,
            actors: item_state.actors
        }
    };
})

return msg;

const {
    payload
} = msg;

let relays_state = {...global.get('relays_state') || {}};

const newPayload = payload.map(item => {
    const {
        relay_type,
        relay_position,
        vendor_id,
        mqtt_topic,
        state,
        actor,
        mode
    } = item;

    let relay_type_state = relays_state[relay_type] || {};
    let mqtt_topic_state = relay_type_state[mqtt_topic] || {};

    let item_state;
    if(relay_type === 'relay') {
        item_state = relay_type_state[relay_position] || {};
    } else if(relay_type === 'wifi_relay') {
        item_state = mqtt_topic_state[relay_position] || {};
    }

    item_state.actors ||= [];

    if(mode === 'subscribe') {
        item_state.state = state;
        if (!item_state.actors.find(({actor: a}) => a === actor)) {
            item_state.actors.push({ actor })
        }
    } else if(mode === 'unsubscribe') {
        item_state.actors = item_state.actors.filter(({actor: a}) => a !== actor);
        if(item_state.actors.length === 0) {
            item_state.state = false;
        }
    }

    if(relay_type === 'relay') {
        relay_type_state[relay_position] = item_state;
    } else if(relay_type === 'wifi_relay') {
        mqtt_topic_state[relay_position] = item_state;
        relay_type_state[mqtt_topic] = mqtt_topic_state;
    }

    relays_state[relay_type] = relay_type_state;

    return {
        relay_type,
        relay_position,
        vendor_id,
        mqtt_topic,
        state: item_state.state
    }
});

global.set('relays_state', relays_state);

return {
    ...msg,
    payload: newPayload
};

